package LintCode;

import java.util.ArrayList;
import java.util.List;

public class Solution_12 {
	
	/***Substring Anagrams*/
	/**
	 * Given a string s and a non-empty string p, find all the start indices of p's anagrams in s.
	 * Strings consists of lowercase English letters only and the length of both strings s and p will not be larger than 40,000.
	 * The order of output does not matter.
	 * **/
	/**
	 * Given s = "cbaebabacd" p = "abc"
	 * return [0, 6]
	 * The substring with start index = 0 is "cba", which is an anagram of "abc".
	 * The substring with start index = 6 is "bac", which is an anagram of "abc".
	 * */
	/**
     * @param s a string
     * @param p a non-empty string
     * @return a list of index
     */
    public List<Integer> findAnagrams(String s, String p) {
        // 一个月之前在leetcode上也做了同样一道题目，当时怎么也做不出来
    	//这次总算是做出来了，算法能力明显提高
    	//但是第一次提交  结果是   Time Limit Exceeded 53%数据通过
    	//当测试的数据是这组时：/LeetCode/src/TempData/5.in ，测试通过53%
    	
    	ArrayList<Integer> result = new ArrayList<Integer>();
    	int sLen =s.length();
    	int pLen =p.length();
    	if(sLen<pLen){
    		return result;
    	}
    	char []sArr = s.toCharArray();
    	char []pArr = p.toCharArray();
    	if(is_single(sArr)&&is_single(pArr)){
    		if(sArr[0]==pArr[0]){
    			for(int i=0;i<sLen-pLen+1;i++){
    				result.add(i);
    			}
    		}
    	}
    	for(int i=0;i<sLen-pLen+1;i++){
			if(is_agram(copyOfRange(sArr, i,i+pLen),copyOf(pArr, pLen))){
				//if(!result.contains(i)){
				if(!contains(result,i)){
					result.add(i);
				}
			}
    	}
    	return result;
    }
    public boolean is_single(char a[]){
    	int len = a.length;
    	for(int i=1;i<len;i++){
    		if(a[i-1]!=a[i]){
    			return false;
    		}
    	}
    	return true;
    }
    //又造了一个轮子，原型中有太多捕获异常的代码，肯定会拖慢算法
    public boolean contains(List<Integer> result, int tar){
    	//原型是    boolean java.util.List.contains(Object o)
    	boolean flag = false;
    	int len = result.size();
    	for(int temp =0;temp<len;temp++){
    		if(result.get(temp)==tar){
    			flag = true;
    			break;
    		}
    	}
    	return flag;
    }
    //重新创造了这两个轮子，然而并没有从本质上优化算法
    public char [] copyOfRange(char [] s,int start,int end){
    	//原型是 Arrays.copyOfRange
    	char [] res = new char[end-start];
    	int index=0;
    	for(int i=start;i<end;i++){
    		res[index++]=s[i];
    	}
    	return res;
    }
    public char [] copyOf(char []s ,int len ){
    	//原型是 Arrays.copyOf 
    	char [] res = new char[len];
    	int index=0;
    	for(int i=0;i<len;i++){
    		res[index++] = s[i];
    	}
    	return res;
    }
    public boolean is_agram(char [] a,char []b){
    	boolean result = true;
    	int len = a.length;
    	boolean flag=false;
    	out:for(int i=0;i<len;i++){
    		if(a[i]=='0')continue;
    		flag=false;
    		for(int j=0;j<len;j++){
    		//	if(b[j]=='0')continue;
    			if(a[i]==b[j]){
    				a[i]=b[j]='0';
    				flag = true;
    			}
    		}
    		if(flag==false){
    			result =false;
    			break out;
    		}
    	}
    	return result;
    }
    public static void main(String args[]){
    	//String s = "cbaebabacd";
    	//String p = "abc";
    	//String s = "abab";
    	//String p = "ab";
    	//String   s = "";
    	//String   p="";
    	//String s = "aaaaaa";
    	//String p = "aaa";
    	String s = "";
    	String p = "";
    	List<Integer> result = (new Solution_12()).findAnagrams(s, p);
    	for(int a : result){
    		System.out.print("  "+a);
    	}
    	//char a[] = {'a','b','c'};
    	//char b[] = {'c','b','a'};
    	//System.out.println((new Solution_12()).is_agram(a, b));
    }
}
