    public String longestPalindrome(String s) {
        if(s==null||s.length()==0){
        	return "";
        }
        String result = "";
        int count = 0;
        int len = s.length();
        char [] sArr = s.toCharArray();
        for(int i=0;i<len-1;i++){
        	for(int j=i+1;j<len;j++){
        		//String temp =s.substring(i, j+1);
        		char [] tempArr = Arrays.copyOfRange(sArr, i, j+1);
        		if(j-i<=count){
        			continue;
        		}
        		if(is_Palindromic(tempArr)){
    				result = new String(tempArr);
    				count=j-i;
    				
        		}else{
        			if(len>100){
	        			int add = (j-i+1);
	        				j+=(add/10);
        			}
        			
        		}
        	}
        }
        if(result.equals("")){
        	result =s.substring(0,1);
        }
        return result;
    }
